var DB_URL;

if (process.env.NODE_ENV == "production"){
    DB_URL = process.env.MONGO_URL;
    console.log(DB_URL);

}

else if (process.env.NODE_ENV == "test"){
    DB_URL = process.env.MONGO_URL_TEST;
    console.log(DB_URL);

}

else{
    DB_URL = process.env.MONGO_URL_DEFAULT;
    console.log(DB_URL);
}

const connectDB = async () => {
    try{
        //mongodb connection string
        const con = await mongoose.connect(DB_URL, {
            useNewUrlParser:true,
            useUnifiedTopology:true
            // useFindAndModify:true,
            // useCreateIndex:true
        })
         if (process.env.NODE_ENV == "production"){
            console.log(`mongoDB connected:${con.connection.host} and is running in
            the ${process.env.NODE_ENV.toLocaleUpperCase()} MODE`);
        }
        
        else if (process.env.NODE_ENV == "test"){
            console.log(`mongoDB connected:${con.connection.host} and is running in
         the ${process.env.NODE_ENV.toLocaleUpperCase()} MODE`);
        }
        
        else{
            console.log(`mongoDB connected:${con.connection.host} and is running in
            the DEFAULT MODE`);
        }
    } catch(error){
        console.log(error);
        process.exit(1);
    }
}

export default connectDB ;

import express from 'express';
const route = express.Router();
import {
    allBlogsRoutes, addBlogRoutes, updateBlogRoutes,
    deleteBlogRoutes, homeRoutes
} from '../services/render';

import { create, find, update, delet } from '../controller/blogController';
import requireAuth from '../middleware/authMiddleware';

route.get('/', homeRoutes);
route.get('/all-blog', requireAuth, allBlogsRoutes);
route.get('/add-blog', requireAuth, addBlogRoutes);
route.get('/update-blog', requireAuth, updateBlogRoutes);
route.get('/delete-blog', requireAuth, deleteBlogRoutes);


// API then 
if (process.env.NODE_ENV == "test") {
    route.post('/api/blogs', create);
    route.get('/api/blogs', find);
    route.get('/api/blogs/:id', find);
    route.put('/api/blogs/:id', update);
    route.delete('/api/blogs/:id', delet);
