else {
    route.post('/api/blogs', requireAuth, create);
    route.get('/api/blogs', find);
    route.get('/api/blogs/:id', find);
    route.put('/api/blogs/:id', requireAuth, update);
    route.delete('/api/blogs/:id', requireAuth, delet);
